import React, { useState } from 'react';
import TodoList from './component/TodoList';
import User from './component/User';

function App() {
  const [selectedUsers, setSelectedUsers] = useState([]); // Manage selected users for filtering
  const [completedFilter, setCompletedFilter] = useState('all'); // Manage completed status filter

  // Handle user filter change (toggle user selection)
  const handleUserFilterChange = (userId) => {
    if (selectedUsers.includes(userId)) {
      setSelectedUsers(selectedUsers.filter((id) => id !== userId)); // Deselect user
    } else {
      setSelectedUsers([...selectedUsers, userId]); // Select user
    }
  };

  // Handle completed status filter change
  const handleCompletedFilterChange = (status) => {
    setCompletedFilter(status);
  };

  return (
    <div className='container-fluid'>
      <div className='row'>
        <div className='col-md-8'>
          <TodoList selectedUsers={selectedUsers} completedFilter={completedFilter} />
        </div>
        <div className='col-md-4'>
          <User
            selectedUsers={selectedUsers}
            onUserFilterChange={handleUserFilterChange}
            completedFilter={completedFilter}
            onCompletedFilterChange={handleCompletedFilterChange}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
