import React, { useContext } from 'react';
import AppContext from '../provider/Context';

function User() {
  const { users, todos, completedFilter, selectedUsers, setCompletedFilter } = useContext(AppContext);

  // Handle user checkbox changes
  // const handleUserChange = (e) => {
  //   const value = parseInt(e.target.value);
  //   setSelectedUsers((prevSelected) => 
  //     e.target.checked ? [...prevSelected, value] : prevSelected.filter((id) => id !== value)
  //   );
  // };

  // Handle completion status radio changes
  // const handleCompletionChange = (e) => {
  //   setCompletedFilter(e.target.value);
  // };

// handle click on user checkbox


// handle click on completed radio box
const handleCompletedFilterChange = (e) => {
  setCompletedFilter(e.target.value);
}

  return (
    <div>
      <h3>Users</h3>
      {users.map((user) => (
        <div key={user.id}>
          <input type="checkbox" value={user.id} />
          {user.name}
        </div>
      ))}
      <h2>Completed</h2>
      <div>
        <input type="radio" name="completedFilter" value="finished" onChange={handleCompletedFilterChange}  />
        Finished
      </div>
      <div>
        <input type="radio" name="completedFilter" value="unfinished"  />
        Unfinished
      </div>
      <div>
        <input type="radio" name="completedFilter" value="all" />
        All
      </div>
    </div>
  );
}

export default User;
