import React from "react";
import { useContext } from "react";
import AppContext from "../provider/Context";
function User() {
  const { users } = useContext(AppContext);
  return (
    <div>
      <h3>Users</h3>
      {users.map((user) => (
        <div key={user.id}>
          <input type="checkbox" value={user.id} />
          {user.name}
        </div>
      ))}
      <h2>Completed</h2>
      <div>
        <input type="radio" name="completedFilter" value="finished" />
        Finished
      </div>
      <div>
        <input type="radio" name="completedFilter" value="unfinished" />
        Unfinished
      </div>
      <div>
        <input type="radio" name="completedFilter" value="all" checked/>
        All
      </div>
    </div>
  );
}

export default User;
