import React, { useContext } from 'react';
import AppContext from '../provider/Context';

function User({ selectedUsers, onUserFilterChange, completedFilter, onCompletedFilterChange }) {
  const { users } = useContext(AppContext);

  return (
    <div>
      <h3>Users</h3>
      {users.map((user) => (
        <div key={user.id}>
          <input
            type="checkbox"
            value={user.id}
            checked={selectedUsers.includes(user.id)}
            onChange={() => onUserFilterChange(user.id)}
          />
          {user.name}
        </div>
      ))}

      <h3>Completed</h3>
      <div>
        <input
          type="radio"
          name="completedFilter"
          value="finished"
          checked={completedFilter === 'finished'}
          onChange={() => onCompletedFilterChange('finished')}
        />
        Finished
      </div>
      <div>
        <input
          type="radio"
          name="completedFilter"
          value="unfinished"
          checked={completedFilter === 'unfinished'}
          onChange={() => onCompletedFilterChange('unfinished')}
        />
        Unfinished
      </div>
      <div>
        <input
          type="radio"
          name="completedFilter"
          value="all"
          checked={completedFilter === 'all'}
          onChange={() => onCompletedFilterChange('all')}
        />
        All
      </div>
    </div>
  );
}

export default User;
