import React, { useState, useEffect } from "react";
import { Container, Form, Row, Col, Table, Button } from "react-bootstrap";
import courses from "./Courses";
import qualities from "./Qualities";

function App() {
  // State for the form data and the list of teachers
  const [formData, setFormData] = useState({
    name: "",
    birthdate: "",
    teachingQuality: "",
    selectedCourses: [],
  });

  const [teachers, setTeachers] = useState([]);
  const [searchInput, setSearchInput] = useState("");
  const [filteredTeachers, setFilteredTeachers] = useState([]);

  useEffect(() => {
    // Load teachers from local storage on component mount
    const storedTeachers = JSON.parse(localStorage.getItem("teachers")) || [];
    setTeachers(storedTeachers);
    setFilteredTeachers(storedTeachers); // Initialize filtered teachers
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;
    setFormData((prevData) => {
      const selectedCourses = checked
        ? [...prevData.selectedCourses, value]
        : prevData.selectedCourses.filter((course) => course !== value);
      return { ...prevData, selectedCourses };
    });
  };

  const handleSearchChange = (e) => {
    setSearchInput(e.target.value);
  };

  const handleSearch = () => {
    // Filter teachers based on the search input
    const filtered = teachers.filter((teacher) =>
      teacher.name.toLowerCase().includes(searchInput.toLowerCase())
    );
    setFilteredTeachers(filtered);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Create a new teacher object
    const newTeacher = {
      id: teachers.length + 1, // Simple ID generation
      name: formData.name,
      dob: formData.birthdate,
      qualities: formData.teachingQuality,
      courses: formData.selectedCourses.map((course) => {
        return courses.find((c) => c.name === course); // Find the course object
      }),
    };

    // Update the list of teachers
    const updatedTeachers = [...teachers, newTeacher];
    setTeachers(updatedTeachers);
    setFilteredTeachers(updatedTeachers); // Add new teacher to filtered list

    // Save to local storage
    localStorage.setItem("teachers", JSON.stringify(updatedTeachers));

    // Reset form data
    setFormData({
      name: "",
      birthdate: "",
      teachingQuality: "",
      selectedCourses: [],
    });
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col>
          <p className="bg-success-subtle text-success rounded-2 p-3">
            INFORMATION DISCLOSURE
          </p>
        </Col>
      </Row>
      <Form onSubmit={handleSubmit}>
        <Row className="mb-3">
          <Col sm={6}>
            <Form.Group controlId="name">
              <Form.Label>Name:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Input your name"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
            </Form.Group>
          </Col>
          <Col sm={6}>
            <Form.Group controlId="birthdate">
              <Form.Label>Birthdate:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Input your birthdate"
                name="birthdate"
                value={formData.birthdate}
                onChange={handleInputChange}
                required
              />
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <Col sm={6}>
            <Form.Group controlId="teachingQuality">
              <Form.Label>Teaching quality:</Form.Label>
              <Form.Select
                name="teachingQuality"
                value={formData.teachingQuality}
                onChange={handleInputChange}
                required
              >
                <option value="">Select quality</option>
                {qualities.map((quality, index) => (
                  <option key={index} value={quality}>
                    {quality}
                  </option>
                ))}
              </Form.Select>
            </Form.Group>
            <Button type="submit" variant="outline-primary m-2 mt-3">
              ADD
            </Button>
            <Button type="button" variant="outline-primary m-2 mt-3" onClick={handleSearch}>
              SEARCH
            </Button>
          </Col>
          <Col sm={6}>
            <Form.Group controlId="courses">
              <Form.Label>Courses:</Form.Label>
              {courses.map((course) => (
                <Form.Check
                  key={course.id}
                  type="checkbox"
                  label={course.name}
                  id={`course-${course.id}`} // Added an id for accessibility
                  value={course.name}
                  checked={formData.selectedCourses.includes(course.name)}
                  onChange={handleCheckboxChange}
                />
              ))}
            </Form.Group>
          </Col>
        </Row>
        {/* Input for Search */}
        <Row className="mb-3">
          <Col sm={12}>
            <Form.Group controlId="search">
              <Form.Label>Search by Name:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Search for a teacher"
                value={searchInput}
                onChange={handleSearchChange}
              />
            </Form.Group>
          </Col>
        </Row>
      </Form>
      {/* List of information */}
      <h3 className="mt-2">List of Information:</h3>
      <Table striped bordered hover>
        <thead className="table-danger">
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Birthdate</th>
            <th>Teaching Quality</th>
            <th>Courses</th>
          </tr>
        </thead>
        <tbody className="table-success">
          {filteredTeachers.map((teacher, index) => (
            <tr key={teacher.id}>
              <td className="fw-bold">{index + 1}</td>
              <td>{teacher.name}</td>
              <td>{teacher.dob}</td>
              <td>{teacher.qualities}</td>
              <td>
                {teacher.courses.map((course) => (
                  <span key={course.id}>
                    {course.name}
                    <br />
                  </span>
                ))}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
}

export default App;
